create database Pregnancy;
-- server --
-- -- đăng kí, đăng nhập, mở rộng chức năng up ảnh mẹ và bé,...
create table users(id int primary key auto_increment, email varchar(100), passwords varchar(50), full_name varchar(100), conceived_date date);

-- -- summary_info: thông tin tuần thai, tình trạng cơ thể mẹ và bé
-- -- symptom: các triệu chứng của mẹ bầu trong mỗi tuần (nhiều triệu chứng trong 1 tuần)
-- -- advice: lời khuyên dành cho mẹ bầu trong mỗi tuần (nhiều lời khuyên cho 1 tuần)
-- -- Glossary: Thuật ngữ và giải thích nghĩa => Viết thuật toán kiểm tra (trong app) hễ thông tin có xuất hiện từ nào trong bảng thuật ngữ thì cho hiển thị ở cuối trang
create table Summary_Info(weeks int primary key auto_increment, baby_info varchar(1000), mom_info varchar(1000));
create table Symptom (id int primary key auto_increment, week_id int, detail varchar (1000));
create table Advice(id int primary key auto_increment, week_id int, detail varchar(1000));
create table Glossary(id int primary key auto_increment, word varchar(100), meaning varchar(1000));
alter table Advice add foreign key  advice_week_id(week_id) references Summary_Info(weeks);
alter table Symptom add foreign key symptom_week_id(week_id) references Summary_Info(weeks);

-- --  Gợi ý thức ăn cho mẹ bầu trong mỗi tuần
create table Food(id int primary key auto_increment, food_name varchar(100), detail varchar(1000));
create table Suggested_Food(id int primary key auto_increment, food_id  int, week_id int);
alter table Suggested_Food add foreign key  suggested_food_food_id(food_id) references Food(id);
alter table Suggested_Food add foreign key  suggested_food_week_id(week_id) references Summary_Info(weeks);

-- --  Gợi ý thể thao dành cho mẹ bầu trong mỗi tuần
create table Sport (id int primary key auto_increment, sport_name varchar(100), detail varchar(1000));
create table Suggested_Sport (id int primary key auto_increment, sport_id  int, week_id int);
alter table Suggested_Sport add foreign key  suggested_sport_sport_id(sport_id) references Sport(id);
alter table Suggested_Sport add foreign key  suggested_sport_week_id(week_id) references Summary_Info(weeks);

